import React, { useState, useEffect, useCallback } from "react";
import "./App.css";
import Search from "./searchCountry";
import Country from "./renderCountry";
import Countries from "./countries";

function App() {
  const [countryData, setCountryData] = useState({});
  const [neighborData, setNeighborData] = useState({});
  const [searchInput, setSearchInput] = useState("");

  const fetchCountryData = useCallback(
    (countryName) => {
      fetch(`https://restcountries.com/v3.1/name/${countryName}`)
        .then((response) => response.json())
        .then((data) => {
          if (data && data.length > 0) {
            setCountryData(data[0]);
            const neighbor = data[0].borders[0];
            if (neighbor) {
              fetchNeighborData(neighbor);
            }
          }
        })
        .catch((error) => console.error(error));
    },
    [fetchNeighborData]
  );

  const fetchNeighborData = useCallback((alphaCode) => {
    fetch(`https://restcountries.com/v3.1/alpha/${alphaCode}`)
      .then((response) => response.json())
      .then((data) => setNeighborData(data[0]))
      .catch((error) => console.error(error));
  }, []);

  useEffect(() => {
    if (searchInput) {
      fetchCountryData(searchInput);
    }
  }, [searchInput, fetchCountryData]);

  return (
    <div className="App">
      <Search setSearchInput={setSearchInput} />
      <Country data={countryData} isNeighbor={false} />
      <Country data={neighborData} isNeighbor={true} />
      <Countries />
    </div>
  );
}

export default App;
